mapper.insert:
INSERT INTO day_flight(day, flightId) VALUES

    #{e},
    #{id}
;

    INSERT INTO schedule (fromDate, toDate, flightId, period) VALUES (#{schedule.fromDate}, #{schedule.toDate},
    #{id}, #{schedule.period})
;

mapper.update:
UPDATE flight SET flightName = #{flight.flightName}, planeName = #{flight.planeName}, fromTown = #{flight.fromTown}, toTown =
#{flight.toTown},
start = #{flight.start}, duration = #{flight.duration}, priceBusiness = #{flight.priceBusiness}, priceEconomy = #{flight.priceEconomy},
approved = #{approved} WHERE id = #{id};


    UPDATE schedule SET fromDate = #{flight.schedule.fromDate}, toDate = #{flight.schedule.toDate}, period =
    #{flight.schedule.period}
    WHERE flightId = #{flight.id}
;

DELETE FROM day_flight WHERE flightId = #{flight.id};

INSERT INTO day_flight(day, flightId) VALUES

    #{e},
    #{flight.id}
;

DELETE FROM places WHERE flightId = #{flight.id};

INSERT INTO places VALUES


        #{p.place},
        #{p.row},
        #{d},
        #{flight.id},
        TRUE,
        #{p.type}

;

UPDATE flight
SET approved = TRUE
WHERE id = #{flightId}

mapper.select:
SELECT f.id AS f_id, f.flightName, f.planeName, f.fromTown, f.toTown, f.start, f.duration, f.priceBusiness, f.priceEconomy, f.approved, s.fromDate, s.toDate, s.period, p.name, p.bussinesRows, p.economyRows, p.placesInBusinessRow, p.placesInEconomyRow, df.day FROM flight f LEFT JOIN schedule s ON f.id = s.flightId RIGHT JOIN plane p ON f.planeName = p.name RIGHT JOIN day_flight df ON f.id = df.flightId
f.flightName=#{flightName}
AND f.planeName = #{planeName}
AND f.fromTown = #{fromTown}
AND f.toTown = #{toTown}
AND #{fromDate} BETWEEN s.fromDate AND s.toDate
AND #{toDate} BETWEEN s.fromDate AND s.toDate
