mapper.select:
SELECT o.orderId, o.userId, o.totalPrice, o.flightDateId,
fd.flightId,
p.ticket, p.firstName, p.lastName, p.nationality, p.passport, p.orderClass, p.price, p.place,
u.id AS u_id, u.lastName AS u_lastName, u.firstName AS u_firstName, u.userType, u.password, u.patronymic AS u_patronymic, u.login,
f.flightName, f.planeName, f.fromTown, f.toTown,
s.toDate, s.fromDate
FROM orders o RIGHT JOIN flight_date fd ON o.flightDateId = fd.id RIGHT JOIN passenger p ON o.orderId = p.orderId RIGHT JOIN user u ON o.userId = u.id RIGHT JOIN flight f ON fd.flightId = f.id LEFT JOIN schedule s ON f.id = s.flightId
f.flightName=#{flightName}
AND f.planeName = #{planeName}
AND f.fromTown = #{fromTown}
AND f.toTown = #{toTown}
AND #{fromDate} BETWEEN s.fromDate AND s.toDate
AND #{toDate} BETWEEN s.fromDate AND s.toDate
AND u.id = #{clientId}
